import React from "react";
import { Bar } from 'react-chartjs-2';
import {
    Chart as ChartJs,
    BarElement,
    CategoryScale,
    LinearScale,
    Tooltip,
    Legend

} from 'chart.js'

ChartJs.register(
    BarElement,
    CategoryScale,
    LinearScale,
    Tooltip,
    Legend
)

const Histogram = ({ data }) => {
    const labels = data.map((item) => item.word);
    const values = data.map((item) => item.frequency);

    const chartData = {
        labels: labels,
        datasets: [
            {
                label: "Frequency",
                data: values,
                backgroundColor: "rgba(75,192,192,1)",
                borderColor : 'black',
                borderRadius : '20px',
                border : '3px solid black'
            },
        ]
    }
    const options = {
        plugins: {
          legend: {
            display: false
          }
        },
        scales: {
          y: {
            beginAtZero: true,
            ticks: {
              stepSize: 1
            }
          }
        }
      }
    
    return (
        <div>
            <Bar data={chartData} options={o}/>
        </div>
    )
};

export default Histogram;
