import React from "react";
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJs,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
} from 'chart.js'

ChartJs.register(
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
)

const Histogram = ({ data }) => {
  const labels = data.map((item)=>item.word);
  const values = data.map((item)=>item.frequency);

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "Frequency",
        data: values,
        backgroundColor: "rgba(103, 58, 183, 0.5)",
        borderColor: 'rgba(103, 58, 183, 1)',
        borderWidth: 1,
        base : 1,
        borderRadius : 2,
        barPercentage :1.,
        hoverBackgroundColor: 'rgba(103, 58, 183, 0.8)',
        hoverBorderColor: 'rgba(103, 58, 183, 1)',
        boxShadow: 'rgba(0, 0, 0, 0.35) 0 5 15',
      },
    ]
  }

  
  return (
    <div style={{ width: '100%', height: '300px', background: '#fff', borderRadius: '10px', boxShadow: '0px 0px 20px rgba(0, 0, 0, 0.1)', overflow: 'hidden' }}>
      <Bar data={chartData} />
    </div>)
}

export default Histogram;
