import React from "react";
import '/Hist'
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJs,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
} from 'chart.js'

ChartJs.register(
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
)

const Histogram = ({ data }) => {
  const labels = data.map((item)=>item.word);
  const values = data.map((item)=>item.frequency);

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "Frequency",
        data: values,
        backgroundColor: '#5499C7',
        base : 1,
        borderRadius : 2,
        hoverBackgroundColor: '#DFFF00',
        
      },
    ]
  }

  
  return (
    <div className="chart">
      <Bar data={chartData} />
    </div>)
}

export default Histogram;
