import React from "react";
import './Histogram.css'
import ExportButton from "./ExportButton";
import { Bar } from 'react-chartjs-2';
import ChartDataLabels from 'chartjs-plugin-datalabels';

import {
  Chart as ChartJs,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
} from 'chart.js'
ChartJs.plugins.register(ChartDataLabels);
ChartJs.register(
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
)

const Histogram = ({ data }) => {
  const labels = data.map((item)=>item.word);
  const values = data.map((item)=>item.frequency);

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "Top 20 words",
        data: values,
        backgroundColor: '#02216F',
        base :1,
        borderRadius : 2,
        hoverBackgroundColor: '#2874A6',
        datalabels: {
          display: true,
          borderWidth: 1,
          borderRadius: 5,
          borderColor: 'rgba(0, 0, 0, 0.2)',
          backgroundColor: 'rgba(0, 0, 0, 0.1)',
          color: 'rgba(0, 0, 0, 0.8)',
          anchor: 'end',
          align: 'end',
          padding: {
            left: 10,
            right: 10,
            top: 5,
            bottom: 5
          },
          offset: 4,
          boxShadow: '2 2 2 #5499C7 '
        }       
      },
    ]
  }
  const options = {
    plugins: {
      legend: {
        labels: {
          color: 'white',
        },
      },
    },
    scales: {
      y: {
        title: {
          display: true,
          text: 'Frequency of each word',
          color: 'white'
        },
        ticks: {
          stepSize: 1,
          color : 'white'
        }
      },
      x: {
        title: {
          display: true,
          text: 'Top 20 Words',
          color: 'white'
        },
        ticks: {
          color : 'white'
        }
      }

    },
    title: {
      display: true,
      text: 'Chart Title',
      color: 'red'
    },
    
  };

  
  return (
    <div className="chart">
      <Bar data={chartData} options={options}/>
      <ExportButton data = {data}/>
    </div>)
}

export default Histogram;
