import React from "react";
import './Histogram.css'
import ExportButton from "./ExportButton";
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJs,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
} from 'chart.js'

ChartJs.register(
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
)

const Histogram = ({ data }) => {
  const labels = data.map((item)=>item.word);
  const values = data.map((item)=>item.frequency);

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "Top 20 words",
        data: values,
        backgroundColor: '#02216F',
        base :1,
        borderRadius : 2,
        hoverBackgroundColor: '#2874A6',       
      },
    ]
  }
  const options = {
    plugins: {
      legend: {
        labels: {
          color: 'white',
        },
      },
    },
    scales: {
      y: {
        title: {
          display: true,
          text: 'Frequency of each word',
          color: 'white'
        },
        ticks: {
          stepSize: 1,
          color : 'white',
        },
        font 
      },
      x: {
        title: {
          display: true,
          text: 'Top 20 Words',
          color: 'white'
        },
        ticks: {
          color : 'white'
        }
      }

    },
    
  };

  
  return (
    <>
    <div className="chart">
      <Bar data={chartData} options={options}/>
    </div>
     <ExportButton data={data}/>
    </>
    )
}

export default Histogram;
