import React from "react";
import './Histogram.css'
import ExportButton from "./ExportButton";
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJs,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
} from 'chart.js'

ChartJs.register(
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
)

const Histogram = ({ data }) => {
  const labels = data.map((item)=>item.word);
  const values = data.map((item)=>item.frequency);

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "Top 20 Words",
        data: values,
        backgroundColor: '#F39C12 ',
        // base :1,
        barThickness: 56,
        borderColor : 'black',
        borderWidth : 2,
        borderRadius : 5,
        hoverBackgroundColor: '#2874A6',       
      },
    ]
  }
  const options = {
    plugins: {
      legend: {
        labels: {
          color: 'white',
        },
      },
    },
    scales: {
      y: {
        title: {
          display: true,
          text: 'Frequency',
          color: 'white',
          font: {
            size: 20,
            weight : 'Bold' 
          }
        },
        ticks: {
          stepSize: 2,
          color : 'white',
          font: {
            size: 16,
            weight : 'Bold' 
          }
        },
        
      },
      x: {
        title: {
          display: true,
          text: 'Words',
          color: 'white',
          font: {
            size: 20,
            weight : 'Bold' 
          }
        },
        ticks: {
          color : 'white',
          font: {
            size: 16,
            weight : 'Bold' 
          }

        }
      }

    },
    
  };

  
  return (
    <>
    <h1 className="header">Histogram of the 20 most occurring words</h1>
    <div className="chart">
      <Bar data={chartData} options={options}/>
    </div>
     <ExportButton data={data}/>
    </>
    )
}

export default Histogram;
